con:config.script:
import com.eviware.soapui.support.*
import groovy.json.JsonSlurper
import jxl.*
import jxl.write.*

//Converting Json Response to String
def response =context.expand('${GetRequest#Response}').toString()
def slurper =new JsonSlurper()
def json = slurper.parseText response
def GetAllProducts=context.testCase
def runner
def counterAssets



//Getting size of json response

counterAssets=json.body.size()
log.info(counterAssets)

//Reading propert values from Custom files

propTest=GetAllProducts.getTestStepByName("Properties")

pidProp=propTest.getPropertyValue("pid").toString()
nameProp=propTest.getPropertyValue("name").toString()
priceProp=propTest.getPropertyValue("price").toString()
discountProp=propTest.getPropertyValue("discount").toString()
newPriceProp=propTest.getPropertyValue("newPrice").toString()

//Validating Json response matches the custome properties
// Comparing the pid value
def pid=json.body.pid[0]
def pidStr=pid.toString()

if(pidStr==pidProp)
{
	log.info("Pass")
}
else
{
	log.info("Fail")
}

//comparing the name

def name=json.body.name[0]
nameStr=name.toString().trim()
if(nameStr==nameProp.toString().trim())
{
	log.info("Pass")
}
else
{
	log.info("Fail")
}

//Comaring the price

def price=json.body.price[0]
def priceStr=price.toString()

if(priceStr==priceProp)
{
	log.info("Pass")
}
else
{
	log.info("Fail")
}

//Comaring the discount

def discount=json.body.discount[0]
def discountStr=discount.toString()

if(discountStr==discountProp)
{
	log.info("Pass")
}
else
{
	log.info("Fail")
}
